class App extends Component{
  constructor(props){
    super(props)
    var shipsPosition = Array(100).fill(null)
    for (var i = 0; i < this.state.remainingShips; i++) {
      shipsPosition[i] = "S"
    }

    function shuffle(array) {
      var currentIndex = array.length, temporaryValue, randomIndex;
      while (0!== currentIndex){
        randomIndex = Math.floor(Math.random()* currentIndex);
        currentIndex -= 1

        temporaryValue = array[currentIndex];
        array[currentIndex] = array[randomIndex];
        array[randomIndex] = temporaryValue
      }
      return array
    }

    shuffle(shipsPosition)
    console.log(shipsPosition);

  this.state = {
    squares:Array(100).fill(null),
    hiddenShips: shipsPosition,
    count: 35,
    remainingShips: 5,
  }
}

handleChange= (index) => {
  var squares = [...this.state.squares]
  squares[index] = this.state.hiddenShips[index] ? this.state.hiddenShips[index] : "X"
  this.setState({squares: squares, count: this.state.count -1, hiddenShips: this.state.hiddenShips -1}, ()=> {
    if(this.state.count === 0) {
      setTimeout(() => {
        alert("you lose")}, 300)

    }
    else if(this.state.hiddenShips === "S" && this.state.hiddenShips === 0 ){
      setTimeout(() => {
        alert("you sank all the ships")}, 300)

    }
  })
}

  render(){
    let square = this.state.squares.map((value, index)=> {
      return (
        <Square value={value} key={index} onClick={() => this.handleChange(index)}/>
      )
    })

    return(
      <React.Fragment>

        <h1>Battleship App</h1>
        <div id= "game-board">
        {square}
        </div>
      </React.Fragment>
    )
  }
}

export default App
